import React, { useState } from 'react';
import Header from './Header';
import HeroSection from './HeroSection';
import CategoriesSection from './CategoriesSection';
import SubcategoriesSection from './SubcategoriesSection';
import ProductsSection from './ProductsSection';
import AboutSection from './AboutSection';
import ContactSection from './ContactSection';
import Footer from './Footer';
import { LoadingSpinner, ErrorMessage } from './ui/LoadingComponents';
import { useProdutos, useCategorias, useSubcategorias } from '../hooks/useApi';

const HomePage = ({ onProductClick }) => {
  const [categoriaAtiva, setCategoriaAtiva] = useState("Todos");
  const [subcategoriaAtiva, setSubcategoriaAtiva] = useState("Todas");

  // Hooks da API
  const { 
    produtos, 
    loading: produtosLoading, 
    error: produtosError 
  } = useProdutos(categoriaAtiva, subcategoriaAtiva, 0, 12);

  const { 
    categorias, 
    loading: categoriasLoading, 
    error: categoriasError 
  } = useCategorias();

  const {
    subcategorias,
    loading: subcategoriasLoading,
    error: subcategoriasError
  } = useSubcategorias();

  const handleCategoryClick = (categoria) => {
    setCategoriaAtiva(categoria);
    setSubcategoriaAtiva("Todas"); // Reset subcategoria quando muda categoria
    // Scroll para a seção de produtos após selecionar categoria
    setTimeout(() => {
      document.getElementById('produtos')?.scrollIntoView({ behavior: 'smooth' });
    }, 100);
  };

  const handleSubcategoryClick = (subcategoria) => {
    setSubcategoriaAtiva(subcategoria);
    // Scroll para a seção de produtos após selecionar subcategoria
    setTimeout(() => {
      document.getElementById('produtos')?.scrollIntoView({ behavior: 'smooth' });
    }, 100);
  };

  // Filtrar subcategorias baseado na categoria ativa
  const subcategoriasFiltradas = subcategorias.filter(sub => {
    if (categoriaAtiva === "Todos") return true;
    return sub.categoria?.toLowerCase() === categoriaAtiva.toLowerCase();
  });

  // Se há erro crítico, mostrar tela de erro
  if (categoriasError && !categorias.length) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-rose-50 to-amber-50 flex items-center justify-center">
        <ErrorMessage 
          error="Não foi possível carregar as categorias" 
          onRetry={() => window.location.reload()} 
        />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-rose-50 to-amber-50">
      <Header />
      <HeroSection />

      {/* Seção de Categorias */}
      {categoriasLoading ? (
        <section className="py-16 px-4">
          <div className="max-w-7xl mx-auto">
            <h3 className="text-4xl font-serif text-amber-800 text-center mb-12">Carregando Categorias...</h3>
            <LoadingSpinner size="xl" className="py-8" />
          </div>
        </section>
      ) : (
        <CategoriesSection 
          categorias={categorias}
          onCategoryClick={handleCategoryClick} 
        />
      )}

      {/* Seção de Subcategorias (opcional - aparece apenas se houver subcategorias) */}
      {!subcategoriasLoading && subcategoriasFiltradas.length > 0 && (
        <SubcategoriesSection
          subcategorias={subcategoriasFiltradas}
          categoriaAtiva={categoriaAtiva}
          subcategoriaAtiva={subcategoriaAtiva}
          onSubcategoryClick={handleSubcategoryClick}
          loading={subcategoriasLoading}
        />
      )}

      {/* Seção de Produtos */}
      <ProductsSection 
        produtos={produtos}
        categorias={categorias.map(cat => cat.nome)}
        categoriaAtiva={categoriaAtiva}
        setCategoriaAtiva={setCategoriaAtiva}
        subcategorias={subcategoriasFiltradas}
        subcategoriaAtiva={subcategoriaAtiva}
        setSubcategoriaAtiva={handleSubcategoryClick}
        onProductClick={onProductClick}
        loading={produtosLoading}
        error={produtosError}
        subcategoriasLoading={subcategoriasLoading}
      />

      <AboutSection />
      <ContactSection />
      <Footer />
    </div>
  );
};

export default HomePage;
