import React from 'react';

const ProductCard = ({ produto, onProductClick }) => {
  return (
    <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg overflow-hidden hover:shadow-xl transition-all hover:-translate-y-1">
      <div className="aspect-[4/3] flex flex-col">
        <div className="relative flex-1">
          <div 
            className="w-full aspect-square flex items-center justify-center text-lg font-serif text-gray-600"
            style={{ backgroundColor: produto.imagem }}
          >
            {produto.nome.split(' ')[0]}
          </div>
          <div className="absolute top-3 left-3 bg-amber-200 text-amber-800 px-2 py-1 rounded-full text-xs font-medium">
            {produto.categoria}
          </div>
        </div>
        <div className="p-3 flex-none">
          <h4 className="text-sm font-semibold text-amber-800 mb-1 line-clamp-1">{produto.nome}</h4>
          <p className="text-amber-600 text-xs mb-2 line-clamp-2">{produto.descricao}</p>
          <div className="flex items-center justify-between">
            <span className="text-sm font-bold text-rose-600">{produto.precoDesconto || produto.preco}</span>
            {produto.precoDesconto && (
              <span className="text-xs text-gray-500 line-through">{produto.preco}</span>
            )}
          </div>
        </div>
      </div>
      <div className="p-3 pt-0">
        <button 
          onClick={() => onProductClick(produto)}
          className="w-full bg-gradient-to-r from-amber-200 to-rose-200 text-amber-800 py-2 rounded-full hover:from-amber-300 hover:to-rose-300 transition-all font-medium text-sm"
        >
          Ver Detalhes
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
