import React, { useState, useRef } from 'react';
import { Heart, Star, Menu, X, Instagram, Facebook, MessageCircle, ChevronLeft, ChevronRight } from 'lucide-react';

const PSCosmeticos = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [currentPage, setCurrentPage] = useState('home');
  const [selectedProduct, setSelectedProduct] = useState(null);
  const carouselRef = useRef(null);

  // Adiciona CSS customizado para scrollbar e line-clamp
  React.useEffect(() => {
    const style = document.createElement('style');
    style.textContent = `
      .scrollbar-hide {
        -ms-overflow-style: none;
        scrollbar-width: none;
      }
      .scrollbar-hide::-webkit-scrollbar {
        display: none;
      }
      .line-clamp-1 {
        display: -webkit-box;
        -webkit-line-clamp: 1;
        -webkit-box-orient: vertical;
        overflow: hidden;
      }
      .line-clamp-2 {
        display: -webkit-box;
        -webkit-line-clamp: 2;
        -webkit-box-orient: vertical;
        overflow: hidden;
      }
    `;
    document.head.appendChild(style);
    return () => document.head.removeChild(style);
  }, []);

  const produtos = [
    {
      id: 1,
      nome: "Sérum Vitamina C",
      categoria: "Skincare",
      preco: "R$ 89,90",
      precoDesconto: "R$ 79,90",
      imagem: "#FFE4E1",
      descricao: "Sérum anti-idade com vitamina C para pele radiante",
      descricaoCompleta: "Nosso Sérum de Vitamina C é formulado com 20% de vitamina C pura e estável, proporcionando uma potente ação antioxidante que combate os radicais livres e estimula a produção de colágeno. Ideal para todos os tipos de pele, este sérum ajuda a uniformizar o tom, reduzir manchas escuras e linhas finas, deixando sua pele com um brilho natural e saudável.",
      ingredientes: ["Vitamina C 20%", "Ácido Hialurônico", "Vitamina E", "Água Termal", "Glicerina"],
      modoUso: "Aplique 2-3 gotas no rosto limpo pela manhã. Massageie suavemente até completa absorção. Use protetor solar durante o dia.",
      rating: 4.8,
      avaliacoes: 127,
      tags: ["Anti-idade", "Clareador", "Antioxidante"]
    },
    {
      id: 2,
      nome: "Shampoo Hidratante",
      categoria: "Haircare",
      preco: "R$ 45,90",
      precoDesconto: null,
      imagem: "#E6F3FF",
      descricao: "Shampoo nutritivo para cabelos secos e danificados",
      descricaoCompleta: "Desenvolvido especialmente para cabelos secos e danificados, este shampoo hidratante contém uma fórmula rica em óleos naturais e proteínas que nutrem profundamente os fios. Sua textura cremosa limpa suavemente sem ressecar, restaurando a maciez, brilho e vitalidade dos cabelos.",
      ingredientes: ["Óleo de Argan", "Proteína de Quinoa", "Manteiga de Karité", "Pantenol", "Água de Coco"],
      modoUso: "Aplique nos cabelos molhados, massageie suavemente o couro cabeludo e fios. Enxágue abundantemente. Repita se necessário.",
      rating: 4.6,
      avaliacoes: 89,
      tags: ["Hidratante", "Nutritivo", "Cabelos Secos"]
    },
    {
      id: 3,
      nome: "Base Líquida HD",
      categoria: "Maquiagem",
      preco: "R$ 67,90",
      precoDesconto: "R$ 59,90",
      imagem: "#FFF0E6",
      descricao: "Base de alta cobertura com acabamento natural",
      descricaoCompleta: "Nossa Base Líquida HD oferece cobertura buildable de média a alta, com acabamento natural e duração de até 12 horas. Sua fórmula livre de óleo é enriquecida com ingredientes hidratantes que cuidam da pele enquanto proporciona um acabamento impecável. Disponível em 20 tons para todos os tipos de pele.",
