import React, { useState } from 'react';
import Header from './Header';
import HeroSection from './HeroSection';
import CategoriesSection from './CategoriesSection';
import ProductsSection from './ProductsSection';
import AboutSection from './AboutSection';
import ContactSection from './ContactSection';
import Footer from './Footer';
import { LoadingSpinner, ErrorMessage } from './ui/LoadingComponents';
import { useProdutos, useCategorias } from '../hooks/useApi';

const HomePage = ({ onProductClick }) => {
  const { 
    categorias, 
    loading: categoriasLoading, 
    error: categoriasError 
  } = useCategorias();

  // Definir categoria ativa baseada nas categorias disponíveis
  const [categoriaAtiva, setCategoriaAtiva] = useState("");

  // Atualizar categoria ativa quando categorias carregarem
  React.useEffect(() => {
    if (categorias.length > 0 && !categoriaAtiva) {
      // Se há uma categoria "Todos", usar ela, senão usar a primeira categoria
      const categoriaTodos = categorias.find(cat => cat.nome === 'Todos');
      setCategoriaAtiva(categoriaTodos ? 'Todos' : categorias[0].nome);
    }
  }, [categorias, categoriaAtiva]);

  const { 
    produtos, 
    loading: produtosLoading, 
    error: produtosError 
  } = useProdutos(categoriaAtiva, 0, 12);

  const handleCategoryClick = (categoria) => {
    setCategoriaAtiva(categoria);
    // Scroll para a seção de produtos após selecionar categoria
    setTimeout(() => {
      document.getElementById('produtos')?.scrollIntoView({ behavior: 'smooth' });
    }, 100);
  };

  // Se há erro crítico, mostrar tela de erro
  if (categoriasError && !categorias.length) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-rose-50 to-amber-50 flex items-center justify-center">
        <ErrorMessage 
          error="Não foi possível carregar as categorias" 
          onRetry={() => window.location.reload()} 
        />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-rose-50 to-amber-50">
      <Header />
      <HeroSection />

      {categoriasLoading ? (
        <section className="py-16 px-4">
          <div className="max-w-7xl mx-auto">
            <h3 className="text-4xl font-serif text-amber-800 text-center mb-12">Carregando Categorias...</h3>
            <LoadingSpinner size="xl" className="py-8" />
          </div>
        </section>
      ) : (
        <CategoriesSection 
          categorias={categorias}
          onCategoryClick={handleCategoryClick} 
        />
      )}

      <ProductsSection 
        produtos={produtos}
        categorias={categorias.map(cat => cat.nome)}
        categoriaAtiva={categoriaAtiva}
        setCategoriaAtiva={setCategoriaAtiva}
        onProductClick={onProductClick}
        loading={produtosLoading}
        error={produtosError}
      />

      <AboutSection />
      <ContactSection />
      <Footer />
    </div>
  );
};

export default HomePage;
