import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { SafeProductCard } from '../components/ui/ProductErrorBoundary';
import { ProductCardSkeleton, ErrorMessage, EmptyState, LoadingSpinner } from '../components/ui/LoadingComponents';
import { useProdutos, useCategorias, useSubcategorias } from '../hooks/useApi';
import { Search, Filter, SortDesc, Grid3x3, LayoutGrid } from 'lucide-react';

const ProductsPage = ({ onProductClick, onBackToHome }) => {
  // Estados para filtros e busca
  const [categoriaAtiva, setCategoriaAtiva] = useState("Todos");
  const [subcategoriaAtiva, setSubcategoriaAtiva] = useState("Todas");
  const [searchTerm, setSearchTerm] = useState("");
  const [sortBy, setSortBy] = useState("relevance");
  const [viewMode, setViewMode] = useState("grid"); // grid ou list
  const [showFilters, setShowFilters] = useState(false);
  const [priceRange, setPriceRange] = useState([0, 1000]);

  // Hooks da API
  const { 
    produtos, 
    loading: produtosLoading, 
    error: produtosError 
  } = useProdutos(categoriaAtiva, subcategoriaAtiva, 0, 24);

  const { 
    categorias, 
    loading: categoriasLoading 
  } = useCategorias();

  const {
    subcategorias,
    loading: subcategoriasLoading
  } = useSubcategorias();

  // Filtrar subcategorias baseado na categoria ativa
  const subcategoriasFiltradas = subcategorias.filter(sub => {
    if (categoriaAtiva === "Todos") return true;
    return sub.categoria?.toLowerCase() === categoriaAtiva.toLowerCase();
  });

  // Filtrar e ordenar produtos
  const produtosFiltrados = produtos
    .filter(produto => {
      // Filtro de busca
      if (searchTerm) {
        const searchLower = searchTerm.toLowerCase();
        return produto.nome.toLowerCase().includes(searchLower) ||
               produto.descricao.toLowerCase().includes(searchLower) ||
               produto.categoria.toLowerCase().includes(searchLower);
      }
      return true;
    })
    .sort((a, b) => {
      switch (sortBy) {
        case 'price-asc':
          return parseFloat(a.preco.replace(/[R$,]/g, '')) - parseFloat(b.preco.replace(/[R$,]/g, ''));
        case 'price-desc':
          return parseFloat(b.preco.replace(/[R$,]/g, '')) - parseFloat(a.preco.replace(/[R$,]/g, ''));
        case 'name':
          return a.nome.localeCompare(b.nome);
        case 'rating':
          return (b.rating || 0) - (a.rating || 0);
        default:
          return 0;
      }
    });

  // Scroll to top quando mudar filtros
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [categoriaAtiva, subcategoriaAtiva]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-rose-50 to-amber-50">
      <Header onBackToHome={onBackToHome} showBackButton={true} />

      {/* Breadcrumb */}
      <div className="bg-white/50 border-b border-amber-100">
        <div className="max-w-7xl mx-auto px-4 py-4">
          <nav className="flex items-center gap-2 text-sm text-amber-700">
            <button onClick={onBackToHome} className="hover:text-amber-800 transition-colors">
              Início
            </button>
            <span>/</span>
            <span className="text-amber-800 font-medium">Produtos</span>
            {categoriaAtiva !== "Todos" && (
              <>
                <span>/</span>
                <span className="text-amber-800 font-medium">{categoriaAtiva}</span>
              </>
            )}
            {subcategoriaAtiva !== "Todas" && (
              <>
                <span>/</span>
                <span className="text-amber-800 font-medium">{subcategoriaAtiva}</span>
              </>
            )}
          </nav>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Header da página */}
        <div className="mb-8">
          <h1 className="text-4xl font-serif text-amber-800 mb-4">
            Catálogo de Produtos
          </h1>
          <p className="text-amber-700 text-lg">
            Explore nossa coleção completa de produtos de beleza e autocuidado
          </p>
        </div>

        {/* Barra de busca e controles */}
        <div className="bg-white/70 backdrop-blur-sm rounded-2xl p-6 mb-8">
          <div className="flex flex-col lg:flex-row gap-4 items-center justify-between">
            {/* Busca */}
            <div className="relative flex-1 max-w-md">
              <Search size={20} className="absolute left-3 top-1/2 -translate-y-1/2 text-amber-600" />
              <input
                type="text"
                placeholder="Buscar produtos..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-3 rounded-xl border border-amber-200 focus:border-amber-400 focus:ring-2 focus:ring-amber-200 transition-all outline-none bg-white/80"
              />
            </div>

            {/* Controles */}
            <div className="flex items-center gap-4">
              {/* Ordenação */}
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="px-4 py-2 rounded-lg border border-amber-200 bg-white/80 text-amber-800 focus:border-amber-400 outline-none"
              >
                <option value="relevance">Relevância</option>
                <option value="name">Nome A-Z</option>
                <option value="price-asc">Menor Preço</option>
                <option value="price-desc">Maior Preço</option>
                <option value="rating">Mais Avaliados</option>
              </select>

              {/* View Mode */}
              <div className="flex bg-amber-100 rounded-lg p-1">
                <button
                  onClick={() => setViewMode('grid')}
                  className={`p-2 rounded transition-all ${
                    viewMode === 'grid' 
                      ? 'bg-white text-amber-800 shadow-sm' 
                      : 'text-amber-600 hover:text-amber-800'
                  }`}
                >
                  <Grid3x3 size={18} />
                </button>
                <button
                  onClick={() => setViewMode('list')}
                  className={`p-2 rounded transition-all ${
                    viewMode === 'list' 
                      ? 'bg-white text-amber-800 shadow-sm' 
                      : 'text-amber-600 hover:text-amber-800'
                  }`}
                >
                  <LayoutGrid size={18} />
                </button>
              </div>

              {/* Toggle Filtros */}
              <button
                onClick={() => setShowFilters(!showFilters)}
                className="flex items-center gap-2 px-4 py-2 bg-amber-200 text-amber-800 rounded-lg hover:bg-amber-300 transition-all"
              >
                <Filter size={18} />
                Filtros
              </button>
            </div>
          </div>
        </div>

        <div className="flex gap-8">
          {/* Sidebar de Filtros */}
          <div className={`${showFilters ? 'block' : 'hidden lg:block'} w-full lg:w-80 space-y-6`}>
            <div className="bg-white/70 backdrop-blur-sm rounded-2xl p-6 sticky top-24">
              {/* Filtro de Categorias */}
              <div className="mb-6">
                <h3 className="text-lg font-semibold text-amber-800 mb-4">Categorias</h3>
                <div className="space-y-2">
                  {!categoriasLoading && categorias.map((categoria) => (
                    <button
                      key={categoria.nome}
                      onClick={() => {
                        setCategoriaAtiva(categoria.nome);
                        setSubcategoriaAtiva("Todas");
                      }}
                      className={`w-full text-left px-3 py-2 rounded-lg transition-all ${
                        categoriaAtiva === categoria.nome
                          ? 'bg-amber-200 text-amber-800 font-medium'
                          : 'text-amber-700 hover:bg-amber-100'
                      }`}
                    >
                      {categoria.nome}
                      {categoria.totalProdutos > 0 && (
                        <span className="float-right text-xs bg-amber-300 text-amber-800 px-2 py-1 rounded-full">
                          {categoria.totalProdutos}
                        </span>
                      )}
                    </button>
                  ))}
                </div>
              </div>

              {/* Filtro de Subcategorias */}
              {!subcategoriasLoading && subcategoriasFiltradas.length > 0 && (
                <div className="mb-6">
                  <h3 className="text-lg font-semibold text-rose-800 mb-4">Subcategorias</h3>
                  <div className="space-y-2">
                    <button
                      onClick={() => setSubcategoriaAtiva("Todas")}
                      className={`w-full text-left px-3 py-2 rounded-lg transition-all ${
                        subcategoriaAtiva === "Todas"
                          ? 'bg-rose-200 text-rose-800 font-medium'
                          : 'text-rose-700 hover:bg-rose-100'
                      }`}
                    >
                      Todas
                    </button>
                    {subcategoriasFiltradas.map((subcategoria) => (
                      <button
                        key={subcategoria.id}
                        onClick={() => setSubcategoriaAtiva(subcategoria.nome)}
                        className={`w-full text-left px-3 py-2 rounded-lg transition-all ${
                          subcategoriaAtiva === subcategoria.nome
                            ? 'bg-rose-200 text-rose-800 font-medium'
                            : 'text-rose-700 hover:bg-rose-100'
                        }`}
                      >
                        {subcategoria.nome}
                      </button>
                    ))}
                  </div>
                </div>
              )}

              {/* Limpar Filtros */}
              {(categoriaAtiva !== "Todos" || subcategoriaAtiva !== "Todas" || searchTerm) && (
                <div className="pt-4 border-t border-amber-200">
                  <button
                    onClick={() => {
                      setCategoriaAtiva("Todos");
                      setSubcategoriaAtiva("Todas");
                      setSearchTerm("");
                    }}
                    className="w-full bg-gray-100 text-gray-700 py-2 rounded-lg hover:bg-gray-200 transition-all"
                  >
                    Limpar Filtros
                  </button>
                </div>
              )}
            </div>
          </div>

          {/* Área de Produtos */}
          <div className="flex-1">
            {/* Contador de Resultados */}
            {!produtosLoading && (
              <div className="mb-6 flex items-center justify-between">
                <p className="text-amber-700">
                  {produtosFiltrados.length} produto{produtosFiltrados.length !== 1 ? 's' : ''} encontrado{produtosFiltrados.length !== 1 ? 's' : ''}
                </p>

                {/* Filtros Ativos */}
                {(categoriaAtiva !== "Todos" || subcategoriaAtiva !== "Todas" || searchTerm) && (
                  <div className="flex flex-wrap gap-2">
                    {categoriaAtiva !== "Todos" && (
                      <span className="bg-amber-200 text-amber-800 px-3 py-1 rounded-full text-sm">
                        {categoriaAtiva}
                      </span>
                    )}
                    {subcategoriaAtiva !== "Todas" && (
                      <span className="bg-rose-200 text-rose-800 px-3 py-1 rounded-full text-sm">
                        {subcategoriaAtiva}
                      </span>
                    )}
                    {searchTerm && (
                      <span className="bg-blue-200 text-blue-800 px-3 py-1 rounded-full text-sm">
                        "{searchTerm}"
                      </span>
                    )}
                  </div>
                )}
              </div>
            )}

            {/* Loading State */}
            {produtosLoading && (
              <div className={`grid gap-6 ${
                viewMode === 'grid' 
                  ? 'grid-cols-1 sm:grid-cols-2 lg:grid-cols-3' 
                  : 'grid-cols-1'
              }`}>
                {[...Array(12)].map((_, index) => (
                  <ProductCardSkeleton key={index} />
                ))}
              </div>
            )}

            {/* Error State */}
            {produtosError && (
              <ErrorMessage 
                error={produtosError} 
                onRetry={() => window.location.reload()}
                title="Erro ao carregar produtos"
              />
            )}

            {/* Empty State */}
            {!produtosLoading && !produtosError && produtosFiltrados.length === 0 && (
              <EmptyState 
                title="Nenhum produto encontrado"
                description={
                  searchTerm 
                    ? `Não encontramos produtos para "${searchTerm}". Tente termos diferentes.`
                    : "Não há produtos disponíveis para os filtros selecionados."
                }
                icon="🔍"
              />
            )}

            {/* Products Grid/List */}
            {!produtosLoading && !produtosError && produtosFiltrados.length > 0 && (
              <div className={`grid gap-6 ${
                viewMode === 'grid' 
                  ? 'grid-cols-1 sm:grid-cols-2 lg:grid-cols-3' 
                  : 'grid-cols-1'
              }`}>
                {produtosFiltrados.map((produto) => (
                  <SafeProductCard 
                    key={produto?.id || Math.random()}
                    produto={produto}
                    onProductClick={onProductClick}
                  />
                ))}
              </div>
            )}
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default ProductsPage;
