import React, { useRef } from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';

const CategoriesSection = ({ onCategoryClick }) => {
  const carouselRef = useRef(null);

  const scrollCarousel = (direction) => {
    if (carouselRef.current) {
      const scrollAmount = 320; // largura do card + gap
      const currentScroll = carouselRef.current.scrollLeft;
      const targetScroll = direction === 'left' 
        ? currentScroll - scrollAmount 
        : currentScroll + scrollAmount;

      carouselRef.current.scrollTo({
        left: targetScroll,
        behavior: 'smooth'
      });
    }
  };

  const categories = [
    {
      name: "Skincare",
      color: "#FFE4E1",
      description: "Produtos para cuidar da sua pele com carinho e delicadeza. Séruns, cremes hidratantes e tratamentos anti-idade.",
      image: "http://localhost:8080/api/v1/image/produto-teste-1b69dceb-f366-4227-970b-a3074f4720fb.png"
    },
    {
      name: "Haircare", 
      color: "#E6F3FF",
      description: "Tudo para cuidar dos seus cabelos. Shampoos, condicionadores, máscaras e tratamentos reconstrutores.",
      image: "https://via.placeholder.com/80x80/ADD8E6/2F4F4F?text=Haircare"
    },
    {
      name: "Maquiagem",
      color: "#FFF0E6", 
      description: "Realce sua beleza natural. Bases, paletas de sombras, batons e produtos para uma maquiagem perfeita.",
      image: "https://via.placeholder.com/80x80/FFE4B5/8B4513?text=Makeup"
    },
    {
      name: "Todos",
      color: "#F0FFE6",
      description: "Uma rotina completa de beleza e bem-estar. Descubra todos os nossos produtos para se cuidar.", 
      image: "https://via.placeholder.com/80x80/90EE90/2F4F4F?text=Care"
    }
  ];

  return (
    <section id="categorias" className="py-16 px-4 overflow-hidden">
      <div className="max-w-7xl mx-auto">
        <h3 className="text-4xl font-serif text-amber-800 text-center mb-12">Nossas Categorias</h3>

        {/* Carrossel de Categorias */}
        <div className="relative">
          {/* Seta Esquerda */}
          <button 
            onClick={() => scrollCarousel('left')}
            className="absolute left-0 top-1/2 -translate-y-1/2 z-10 bg-white/90 hover:bg-white shadow-lg rounded-full p-3 transition-all hover:scale-110"
          >
            <ChevronLeft size={24} className="text-amber-800" />
          </button>

          {/* Seta Direita */}
          <button 
            onClick={() => scrollCarousel('right')}
            className="absolute right-0 top-1/2 -translate-y-1/2 z-10 bg-white/90 hover:bg-white shadow-lg rounded-full p-3 transition-all hover:scale-110"
          >
            <ChevronRight size={24} className="text-amber-800" />
          </button>

          <div 
            ref={carouselRef}
            className="flex gap-6 overflow-x-auto scrollbar-hide pb-4 scroll-smooth px-12" 
            style={{ scrollSnapType: 'x mandatory' }}
          >
            {categories.map((category) => (
              <div 
                key={category.name}
                className="flex-none w-80 bg-white/80 backdrop-blur-sm rounded-2xl overflow-hidden hover:shadow-xl transition-all hover:-translate-y-2 cursor-pointer group"
                onClick={() => onCategoryClick(category.name)}
                style={{ scrollSnapAlign: 'start' }}
              >
                <div 
                  className="w-full h-60 flex items-center justify-center group-hover:scale-105 transition-transform"
                  style={{ backgroundColor: category.color, aspectRatio: '4/3' }}
                >
                  <img 
                    src={category.image} 
                    alt={category.name}
                    className="w-20 h-20 object-contain"
                  />
                </div>
                <div className="p-6">
                  <h4 className="text-2xl font-serif text-amber-800 mb-3">{category.name}</h4>
                  <p className="text-amber-700 mb-4 leading-relaxed text-sm">
                    {category.description}
                  </p>
                  <button className="w-full bg-gradient-to-r from-amber-200 to-rose-200 text-amber-800 py-3 rounded-full hover:from-amber-300 hover:to-rose-300 transition-all font-medium">
                    {category.name === 'Todos' ? 'Ver Todos' : 'Ver Produtos'}
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default CategoriesSection;
