// src/components/DebugPanel.js
import React, { useState } from 'react';

const DebugPanel = () => {
  const [apiStatus, setApiStatus] = useState(null);
  const [isChecking, setIsChecking] = useState(false);

  const checkAPI = async () => {
    setIsChecking(true);
    const results = {};

    // Testar endpoint de produtos
    try {
      const produtosResponse = await fetch('http://localhost:8080/api/v1/produtos');
      const produtosData = await produtosResponse.json();
      results.produtos = {
        status: produtosResponse.status,
        data: produtosData,
        count: produtosData?.data?.content?.length || produtosData?.content?.length || 0
      };
    } catch (error) {
      results.produtos = { error: error.message };
    }

    // Testar endpoint de categorias
    try {
      const categoriasResponse = await fetch('http://localhost:8080/api/v1/categorias');
      const categoriasData = await categoriasResponse.json();
      results.categorias = {
        status: categoriasResponse.status,
        data: categoriasData,
        count: categoriasData?.content?.length || 0
      };
    } catch (error) {
      results.categorias = { error: error.message };
    }

    setApiStatus(results);
    setIsChecking(false);
  };

  return (
    <div className="fixed bottom-4 right-4 bg-white border border-gray-300 rounded-lg p-4 shadow-lg max-w-md z-50">
      <h3 className="font-bold mb-2">üîç Debug API</h3>

      <button 
        onClick={checkAPI}
        disabled={isChecking}
        className="bg-blue-500 text-white px-3 py-1 rounded mb-3 disabled:opacity-50"
      >
        {isChecking ? 'Testando...' : 'Testar API'}
      </button>

      {apiStatus && (
        <div className="space-y-2 text-sm">
          <div>
            <strong>Produtos:</strong>
            {apiStatus.produtos.error ? (
              <span className="text-red-600"> ‚ùå {apiStatus.produtos.error}</span>
            ) : (
              <span className="text-green-600"> ‚úÖ {apiStatus.produtos.count} produtos encontrados</span>
            )}
          </div>

          <div>
            <strong>Categorias:</strong>
            {apiStatus.categorias.error ? (
              <span className="text-red-600"> ‚ùå {apiStatus.categorias.error}</span>
            ) : (
              <span className="text-green-600"> ‚úÖ {apiStatus.categorias.count} categorias encontradas</span>
            )}
          </div>

          <details className="mt-2">
            <summary className="cursor-pointer text-blue-600">Ver dados brutos</summary>
            <pre className="text-xs bg-gray-100 p-2 mt-1 overflow-auto max-h-40">
              {JSON.stringify(apiStatus, null, 2)}
            </pre>
          </details>
        </div>
      )}
    </div>
  );
};

export default DebugPanel;
